import React from 'react';
import '../App.css';
import Section1 from './Section1';
import Section2 from './Section2';
import Section3 from './Section3';

class App extends React.Component {
  constructor(props) {
    super(props);
	this.sectionNames = ["Configurazione", "Pista", "Classifica"];

    this.state = {
		corridori: 0,
		colors: [ "red", "green", "blue", "violet", "yellow", "orange"],
		griglia : [],
		isColored: [],
		posizioni: [],
		started: false,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  printStatus(){
	console.log("Status:");
	console.log("Corridori: " + this.state.corridori);
	console.log("Colors: " + this.state.colors);
	console.log("Posizioni: " + this.state.posizioni);
	console.log("Griglia: " + this.state.griglia);
	console.log("isColored: " + this.state.isColored);

  }

  handleClick(){
	let grigliaTemp = [];
	let isColoredTemp = [];
	let corridoriTemp = parseInt(document.getElementById("corridori").value, 10);

	for(let i=0;i<corridoriTemp;i++){
		//griglia
		grigliaTemp[i] = [];
		grigliaTemp[i][0] = "Partenza";
		grigliaTemp[i][10] = "Traguardo";
		for(let j=1;j<10;j++){
			grigliaTemp[i][j] = j;
		}

		//isColored
		for(let i=0;i<corridoriTemp;i++){
			isColoredTemp[i] = [];
			isColoredTemp[i][0] = true;
			for(let j=1;j<11;j++){
				isColoredTemp[i][j] = false;
			}
		}
	}

	useEffect(() => {
		let intervalId;

		if (this.state.started) {
		  // Start the interval when started is true
		  intervalId = setInterval(() => {
			console.log('Function is running every 4 seconds');


		  }, 4000);
		}

		// Cleanup function to clear the interval
		return () => {
		  clearInterval(intervalId);
		};
	  }, [this.state.started]);


	this.setState(
		({
			griglia: grigliaTemp,
			corridori: corridoriTemp,
			isColored: isColoredTemp,
			started: true,
		}),
		this.printStatus
	);

  }

  modificaCella(i,j){
	    if(this.state.disabled[i][j] === false){
	    	this.setState({
	    		tentativi : this.state.tentativi +1,
	    	});
	    	let punteggioTemp = this.state.punteggio;
	    	let temp = this.state.disabled;
	    	temp[i][j] = true;
	    	punteggioTemp += this.state.griglia[i][j];
	    	if(i-1 >=0 && temp[i-1][j] === false){
	    		temp[i-1][j] = true;
	    		punteggioTemp += this.state.griglia[i-1][j];
	    	}
	    	if(i+1 <this.state.lunghezza && temp[i+1][j] === false){
	    		temp[i+1][j] = true;
	    		punteggioTemp += this.state.griglia[i+1][j];
	    	} 
	    	if(j-1 >=0 && temp[i][j-1] === false){
	    		temp[i][j-1] = true;
	    		punteggioTemp += this.state.griglia[i][j-1];
	    	}
	    	if(j+1 >=0 && temp[i][j+1] === false){
	    		temp[i][j+1] = true;
	    		punteggioTemp += this.state.griglia[i][j+1];
	    	}
	    	this.setState({
	    		punteggio : punteggioTemp,
	    		disabled : temp,
	    	});
	    }
  }

  isFinito(){
    let finish = true;
    for(let i = 0 ; i<this.state.lunghezza && finish === true;i++){
    	for(let j = 0 ;j<this.state.larghezza;j++){
    		if(this.state.disabled[i][j] === false){
    			finish = false;
    			break;
    		}
    	}
    }
    if(finish === true){
    	if(this.state.show === false){
	    	this.setState({
	    		mediaPunt : this.state.punteggio / this.state.tentativi,
	    		show : true,
	    	});
    	}
    	return true;
    }
    else{
    	return false;
    }
  }

  render() {
    return (
      <div className="App">

		<Section1 
			sectionName = {this.sectionNames[0]}
			handleClick={this.handleClick} 
		/>
		<Section2 
			sectionName = {this.sectionNames[1]}
			matrix={this.state.griglia} 
			colors={this.state.colors}
			isColored={this.state.isColored}
		/>
		<Section3 
			sectionName = {this.sectionNames[2]}
		/>

      </div>
    );
  }
}

export default App;
