import React from 'react';
import '../App.css';
import Section1 from './Section1';
import Section2 from './Section2';
import Section3 from './Section3';

class App extends React.Component {
  constructor(props) {
    super(props);
	this.sectionNames = ["Configurazione", "Pista", "Classifica"];

    this.state = {
		corridori: 0,
		colors: [ "red", "green", "blue", "violet", "yellow", "orange"],
		griglia : [],
		isColored: [],
		posizioni: [],
		classifica: [],
		intervalId: null,
		started: false,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  printStatus(){
	console.log("Status:");
	console.log("Corridori: " + this.state.corridori);
	console.log("Colors: " + this.state.colors);
	console.log("Posizioni: " + this.state.posizioni);
	console.log("Griglia: " + this.state.griglia);
	console.log("isColored: " + this.state.isColored);
	console.log("Started: " + this.state.started);

  }

  handleClick(){
	let corridoriTemp = parseInt(document.getElementById("corridori").value, 10);

	if(corridoriTemp < 4 || corridoriTemp > 6){
		alert("Inserisci un numero di corridori tra 4 e 6!")
		return
	}

	let grigliaTemp = [];
	let isColoredTemp = [];
	let posizioniTemp = [];

	for(let i=0;i<corridoriTemp;i++){
		//griglia
		grigliaTemp[i] = [];
		grigliaTemp[i][0] = "Partenza";
		grigliaTemp[i][10] = "Traguardo";
		for(let j=1;j<10;j++){
			grigliaTemp[i][j] = j;
		}

		//isColored
		isColoredTemp[i] = [];
		isColoredTemp[i][0] = true;
		for(let j=1;j<11;j++){
			isColoredTemp[i][j] = false;
		}


		posizioniTemp[i] = 0;
	}

	this.setState(
		({
			griglia: grigliaTemp,
			corridori: corridoriTemp,
			isColored: isColoredTemp,
			posizioni: posizioniTemp,
			started: true,
		}),
		this.printStatus
	);	
	this.startInterval();
  }

  startInterval = () => {
    // Check if an interval is already running
    if (this.state.intervalId) return;

    // Set up the interval
    const intervalId = setInterval(() => {
      this.avanza();
    }, 4000); // 4000 milliseconds = 4 seconds

    // Update the state with the interval ID
    this.setState({ intervalId });
  };

  componentWillUnmount() {
    // Clear the interval when the component unmounts
    const { intervalId } = this.state.intervalId;
    if (intervalId) {
      clearInterval(intervalId);
    }
  }

  avanza(){
	if(this.state.started){
		let isColoredTemp = this.state.isColored;
		let startedTemp = true;
		let posizioniTemp = this.state.posizioni;
		let classificaTemp = this.state.classifica;

		for(let i=0;i<this.state.corridori;i++){
			let lastIndex = -1;
			for(let j=1;j<11;j++){
				if(isColoredTemp[i][j] === false){
					lastIndex = j-1;
					break;
				}

			}

			let n = Math.floor(Math.random() * 3) + 1;
			console.log("Corridore " + (i+1) + " avanza di " + n  + " posizioni");

			for(let j=lastIndex;j<=lastIndex+n;j++){
				isColoredTemp[i][j] = true;
			}
		}

		//aggiorno posizioni
		for(let i=0;i<this.state.corridori;i++){
			let lastIndex = -1;
			for(let j=1;j<11;j++){
				if(isColoredTemp[i][j] === false){
					lastIndex = j-1;
					console.log("Last index of isColored for corridor " + i + ": " + lastIndex);
					break;
				}
				else if(j === 10){
					lastIndex = 10;
				}

			}

			posizioniTemp[i] = lastIndex;
		}

		//aggiorno la classifica


		//controllo se qualcuno è arrivato al traguardo
		for(let i=0; i<posizioniTemp.length;i++){
			if(posizioniTemp[i] === 10){
				startedTemp = false;
			}
		}


		this.setState(
			({
				isColored: isColoredTemp,
				posizioni: posizioniTemp,
				started: startedTemp,
			}),
			this.printStatus,
			startedTemp === false ? setTimeout(() => { alert("Gioco finito"); }, 2000 ) : null,
		)
	}
  }



  modificaCella(i,j){
	    if(this.state.disabled[i][j] === false){
	    	this.setState({
	    		tentativi : this.state.tentativi +1,
	    	});
	    	let punteggioTemp = this.state.punteggio;
	    	let temp = this.state.disabled;
	    	temp[i][j] = true;
	    	punteggioTemp += this.state.griglia[i][j];
	    	if(i-1 >=0 && temp[i-1][j] === false){
	    		temp[i-1][j] = true;
	    		punteggioTemp += this.state.griglia[i-1][j];
	    	}
	    	if(i+1 <this.state.lunghezza && temp[i+1][j] === false){
	    		temp[i+1][j] = true;
	    		punteggioTemp += this.state.griglia[i+1][j];
	    	} 
	    	if(j-1 >=0 && temp[i][j-1] === false){
	    		temp[i][j-1] = true;
	    		punteggioTemp += this.state.griglia[i][j-1];
	    	}
	    	if(j+1 >=0 && temp[i][j+1] === false){
	    		temp[i][j+1] = true;
	    		punteggioTemp += this.state.griglia[i][j+1];
	    	}
	    	this.setState({
	    		punteggio : punteggioTemp,
	    		disabled : temp,
	    	});
	    }
  }

  isFinito(){
    let finish = true;
    for(let i = 0 ; i<this.state.lunghezza && finish === true;i++){
    	for(let j = 0 ;j<this.state.larghezza;j++){
    		if(this.state.disabled[i][j] === false){
    			finish = false;
    			break;
    		}
    	}
    }
    if(finish === true){
    	if(this.state.show === false){
	    	this.setState({
	    		mediaPunt : this.state.punteggio / this.state.tentativi,
	    		show : true,
	    	});
    	}
    	return true;
    }
    else{
    	return false;
    }
  }

  render() {
    return (
      <div className="App">

		<Section1 
			sectionName = {this.sectionNames[0]}
			handleClick={this.handleClick} 
		/>
		<Section2 
			sectionName = {this.sectionNames[1]}
			matrix={this.state.griglia} 
			colors={this.state.colors}
			isColored={this.state.isColored}
		/>
		<Section3 
			sectionName = {this.sectionNames[2]}
			posizioni={this.state.posizioni}
		/>

      </div>
    );
  }
}

export default App;
