import React from 'react';

class Section3 extends React.Component{
	constructor(props){
	   super(props);	
	}

	render(){
		return(
		<>
		   {  
				<>
				<br/>
				<div style={{ textAlign: 'center' }}>
				Posizioni:
				<br/> 
				{this.props.posizioni.map((posizione, corridore) => (
					<div key={corridore}>
						<input 
							type="text" 
							readOnly 
							value={`${posizione}`} 
							style='block'
							/> 
					</div>
				))}
				<input 
					type="text" 
					readOnly 
					value={` ${this.props.posizioni}`} 

				/>
				<input 
				type="text" 
				readOnly 
				value={`Media punteggio per lancio: ${this.props.media}`} 
				style={{display: this.props.show === true ? 'block' : 'none'  }}
				/> 
				</div>
				</>
		   }
		 </>
		);
	}

}
export default Section3;
