import React from 'react';
import '../App.css';
import Compilazione from './Compilazione';
import Visualizzazione from './Visualizzazione';
import Estrazione from './Estrazione';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      numbers: ['', '', '', '', ''],
      drawnNumbers: [],
      result: '',
      selected : false,
      ready : false,
    };
    this.checkResult = this.checkResult.bind(this);
    this.onClick = this.onClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }


  modificaIndiceArray(indice,valore){
  	this.setState(prevState => ({
	    numbers: {
		...prevState.numbers,
		[indice]: valore  // Assegna un nuovo valore a uno specifico indice
	    }
	}));
	return;
  }

  handleChange(event){
  	var indice = parseInt(event.target.name.trim());
  	if(isNaN(indice) || indice<0 || indice>4){
  	   console.log("errore indice:"+indice);
  	}
  	else{
  	    var valore = event.target.value.trim();
  	    if(valore === ""){
  	    	this.modificaIndiceArray(indice,'');
  	    	return;
  	    }
  	    valore = parseInt(valore);
  	    if(isNaN(valore)){
  	    	alert("Devi inserire un numero intero!");
  	    	this.modificaIndiceArray(indice,'');
  	    	return;
  	    }
  	    else{
  	    	if(valore < 1 || valore > 10){
	  	    	 alert("deve essere compreso fra 1 e 10");
	  	    	 this.modificaIndiceArray(indice,'');
	  	    	 return;
  	    	}
  	    	else{
  	    	   this.state.numbers.map((item,index) => { 
  	    	   	if(index != indice && item === valore){
  	    	   		alert("I 5 numeri devono essere distinti!");
  	    	   		this.modificaIndiceArray(indice,'')
  	    	   		return;
  	    	   	}
  	    	   } )
  	    	   this.modificaIndiceArray(indice,valore);
  	    	   return;
  	    	} 
  	    }
  	}
  };

  onClick(event){
  	console.log("entrato");
  	console.log(event.target);
  	console.log(event.target.name);
  	if(event.target.name === 'genera'){
  		this.setState ({
  		    numbers: ['', '', '', '', ''],
  		    drawnNumbers: [],
  		    selected : true,
  		    ready : false,
  		})
  	}
  	else if(event.target.name === 'finalizza' ){
  		this.state.numbers.map((item) => {
  			if(item === ""){
  			   alert("Inserisci tutti i numeri prima di inviare");
  			   return;
  			}
  		})
  		this.setState ({
  		   selected : false,
  		   ready : true,
  		})
  	}
  }
  checkResult(drawnNumbers) {
    const matches = this.state.numbers.filter(num => drawnNumbers.includes(num));
    if (matches.length >= 2) {
      this.setState({ result: 'Hai vinto!' });
    } else {
      this.setState({ result: 'Mi dispiace, non hai vinto' });
    }
  }

  render() {
    return (
      <div className="App">
        <Compilazione onClick={this.onClick} numbers={this.state.numbers} selected={this.state.selected} handleChange={this.handleChange}/>
        <Visualizzazione numbers={this.state.numbers} result={this.state.result} />
        <Estrazione drawnNumbers={this.state.drawnNumbers} checkResult={this.checkResult} />
      </div>
    );
  }
}

export default App;
