import React from 'react';
import Lavagna from './Lavagna';
import Form from './Form';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      nazione: "Italia",
      dati: "",
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();

    if(this.state.email === "") alert("Email mancante");

    else if(this.state.password === "") alert("Password mancante");

    else if(!this.state.email.includes("@")) alert("Email non corretta (@ mancante)");

    else{

      try {
        this.setState({
          dati: this.state.dati + " " + this.state.email + " " + this.state.password + " " + this.state.nazione + " "
        })
      } catch (e) {
        this.setState({
          dati: "error"
        })
      }

    }
  }

  handleClick(e) {
    try {
      this.setState({
        dati: ""
      })
    } catch (e) {
      this.setState({
        dati: "error"
      })
    }
  }

  handleChange(e) {
    if (e.target.name === "email") {
      try {
        this.setState({
          email: e.target.value
        })
      } catch (e) {
        this.setState({
          email: "error"
        })
      }
    }

    else if (e.target.name === "password") {
      try {
        this.setState({
          password: e.target.value
        })
      } catch (e) {
        this.setState({
          password: "error"
        })
      }
    }

    else if (e.target.name === "nazione") {
      try {
        this.setState({
          nazione: e.target.value
        })
      } catch (e) {
        this.setState({
          nazione: "error"
        })
      }
    }

    else alert("error");
  }

  render() {
    return (
      <div>
        <h1>Form</h1>
        <Form email={this.state.email} password={this.state.password} onChange={this.handleChange}
         onClick={this.state.handleSubmit}/>
        <h1>Lavagna</h1>
        <Lavagna dati={this.state.dati} onClick={this.handleClick}/>
      </div>
    );
  }
}

export default App;
