import React from 'react';

class Compilazione extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      numbers: ['', '', '', '', ''],
      result: '',
    };
    this.generateEmptyTicket = this.generateEmptyTicket.bind(this);
    this.finalizeTicket = this.finalizeTicket.bind(this);
    this.updateNumber = this.updateNumber.bind(this);
  }

  generateEmptyTicket() {
    this.setState({ numbers: ['', '', '', '', ''], result: '' });
  }

  finalizeTicket() {
    const validNumbers = this.state.numbers.filter(num => num >= 1 && num <= 10);
    if (validNumbers.length === 5) {
      this.setState({ numbers: validNumbers, result: '' });
    } else {
      this.setState({ result: 'Compilare tutti i numeri (da 1 a 10)' });
    }
  }

  updateNumber(index, value) {
    const updatedNumbers = [...this.state.numbers];
    updatedNumbers[index] = value;
    this.setState({ numbers: updatedNumbers });
  }

  render() {
    return (
      <div className="section">
        <h2>Compilazione</h2>
        <button onClick={this.generateEmptyTicket}>Nuova Schedina</button>
        <div className="numbers">
          {this.state.numbers.map((number, index) => (
            <input
              key={index}
              type="text"
              value={number}
              onChange={(e) => this.updateNumber(index, e.target.value)}
            />
          ))}
        </div>
        <button onClick={this.finalizeTicket}>Finalizza</button>
        <p>{this.state.result}</p>
      </div>
    );
  }
}

export default Compilazione;
