import React from 'react';

class Compilazione extends React.Component{
	constructor(props) {
		super(props);
		this.handleChange = this.handleChange.bind(this);
	}

	handleChange(e){
		console.log(e);
		console.log(e.target);
		console.log(e.target.value);
		console.log(e.target.name);
	}

	render() {	
		return (
	      <>
		<h2>Compilazione</h2>
		<button onClick={this.props.handleClick}>Nuova Schedina</button>
		<div className="section">
		  {this.props.numbers.map((item,index) => <input type="text" style={{display: this.props.selected === true ? 'block' : 'none' }} id={index} name={index} onKeyUp = {this.handleChange} key={index}/>)}
		</div>
		<button name="finalizza" onClick={this.props.handleClick}>Finalizza</button>
	      </>
	    );
    }

}

export default Compilazione;
