import React from 'react';

class Visualizzazione extends React.Component {
  constructor(props) {
		super(props);
	}

	  calculate(numeriUtente,numeriEstratti){
	  	let indici = [];
	  	let i=0;
	  	while(numeriUtente.length < i){
	  	   let j=0;
	  	   while(numeriEstratti.length < j){
	  	   	if(numeriUtente[i] === numeriEstratti[j])
	  	   	   indici.push(i);
	  	   }	
	  	   i++;
	  	}
	  	return indici;
	  }

	render() {	
		console.log(this.props);
		let words = this.props.result;
		let temp = words.split(':');
		let color = temp[0].split();
		return (
	      <>
		<h2>Visualizzazione</h2>
		<input type="text" value={this.props.result} readOnly style={{ display: this.props.show === true ? 'block' : 'none' }} />
		<div className="section">
		  {this.props.numbers.map((item,index) => <input name={index} type="text" style={{display: this.props.ready === true ? 'block' : 'none' }} readOnly key={index} value={item}/>)}
		</div>
	      </>
	    );
    }
}

export default Visualizzazione;
