import React from 'react';

class Estrazione extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      drawnNumbers: this.props.drawnNumbers,
    };
    this.drawNumbers = this.drawNumbers.bind(this);
  }

  drawNumbers() {
    const randomNumbers = [];
    while (randomNumbers.length < 5) {
      const randomNum = Math.floor(Math.random() * 10) + 1;
      if (!randomNumbers.includes(randomNum)) {
        randomNumbers.push(randomNum);
      }
    }
    this.setState({ drawnNumbers: randomNumbers });
    this.props.checkResult(randomNumbers);
  }

  render() {
    return (
      <div className="section">
        <h2>Estrazione</h2>
        <button onClick={this.drawNumbers}>Estrai Numeri</button>
        {this.state.drawnNumbers.map((number, index) => (
          <input key={index} type="text" readOnly value={number} />
        ))}
      </div>
    );
  }
}

export default Estrazione;
