import Display from './ReactComponent/Display.js'
import KeyBoard from './ReactComponent/KeyBoard.js'
import React from 'react';

class App extends React.Component {
  constructor(){
        super();
        this.state = {
        result1: ""
        , result2: ""
        }
        this.onClick = this.onClick.bind(this); 
  }

  onClick(e) {
        let button = e.target.name
        if(this.state.result1===""){
        	this.setState({result2:""})
        }
        if(button === "="){

            this.calculate()
        }

      else if(button === "C"){
          this.reset()
      }
      else if(button === "CE"){
          this.backspace()
      }

      else {
          this.setState({
        result1: this.state.result1 + button
          })
      }
  };


  calculate() {
        try {
              this.setState({
              result2: "Risultato: "+(eval(this.state.result1) || "" ) + "",
              result1: ""
            })
        } catch (e) {
            this.setState({
          result2: "error"
            })

        }
  };

  reset(){
      this.setState({
          result1: ""
          , result2: ""
      })
        };


  backspace(){
      this.setState({
          result1: this.state.result.slice(0, -1)
      })
  };

  render() {
      return (

        <div className="calculator-body">
            <h1>Calcolatrice</h1>
            <Display result1={this.state.result1} result2={this.state.result2}/>
            <KeyBoard onClick={this.onClick}/>
        </div>

      );
  }
}
export default App;
