import React from 'react';

class Compilazione extends React.Component{
	constructor(props) {
		super(props);
		this.state = {
			numbers : ["","","","",""],
			selected : false,
		}
		this.handleChange = this.handleChange.bind(this);
	}

	handleChange(e){
		console.log(e);
		console.log(e.target);
		console.log(e.target.value);
		console.log(e.target.name);
	}

	render() {	
		return (
	      <>
		<h2>Compilazione</h2>
		<button onClick={ () => this.setState ({ selected : true }) }>Nuova Schedina</button>
		<div className="section">
		  {this.state.numbers.map((item,index) => <input type="text" min="1" max="10" style={{display: this.state.selected === true ? 'block' : 'none' }} id={index} name={index} onKeyUp = {this.handleChange} key={index}/>)}
		</div>
		<button onClick={ () => this.setState ({ selected : false }) }>Finalizza</button>
	      </>
	    );
    }

}

export default Compilazione;
